import React, { Component } from "react";
import Cards from "./components/Cards";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import cards from "./cards.json";

class App extends Component {

  state = {
    cards
  };

  removeCard = id => {

    const cards = this.state.cards.filter(card => cards.id !== id);

    this.setState({ cards });
  };


  render() {
    return (
      <Wrapper>
        <Title>Memory Game: Tribute to the Chihuahua</Title>
        <h1>Click on a Chihuahua to earn points, but don't click any Chihuahua more than once!!</h1>
          <Cards
            removeCards={this.removeCards}
            id={cards.id}
            key={card.id}
            name={cards.name}
            image={cards.image}
            occupation={cards.occupation}
            location={cards.location}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
